// fix repeated messages
let millisecondsBetweenMessages = 10;
let previousTime=context.get('time') || 0;
let currentTime=Math.round(Date.now());
let timeDifference=currentTime-previousTime;

// set variable for next round
context.set('time', currentTime);

if (timeDifference < millisecondsBetweenMessages){
    node.warn('To fast: '+timeDifference+'ms');
    msg = null; // stop msg from sending
}

return msg;

let red = msg.payload.r
