// get the default payload
// add the payload of the flow
// add the new values to the flow payload
// safe the new payload
// return the new payload

// a bug in the colorpicker:
//   when the colorpicker gets a 0,0,0 it stops sending changes in color signal
//   therefor I send 123, 123, 123 by default

msg.topic = "shellies/shellyrgbw2/color/0/set"

// msg.payload["On"]

defaultPayload = {
    "mode": "color",
    "red": 0,
    "green": 0,
    "blue": 255,
    "gain": 50,
    "white": 0,
    "effect": 0,
    "turn": "off"
}

// merge payloads & remove all unneeded keys
function mergePayloads(dest, src) {
    for(var key in src) {
        if (key in defaultPayload){
            dest[key] = src[key];
        }
    } 
    return dest; 
}

// default + flow
var newPayload = mergePayloads(defaultPayload, flow.get('flowPayload'));

// flow + new
newPayload = mergePayloads(newPayload, msg.payload);

// store new payload in flow
flow.set('flowPayload', newPayload)

// get the data from the flow variable (so we get we stored)
msg.payload = flow.get('flowPayload')

// return the new payload
return msg;

// create message
msg.payload = {"turn": msg.payload}

// return message
return msg;

msg.payload = { "r" : msg.payload.red,
                "g" : msg.payload.green,
                "b" : msg.payload.blue
                };
return msg;

// create message
msg.payload = {"white": msg.payload}

// return message
return msg;

// create message
msg.payload = {"effect": msg.payload}

// return message
return msg;

var myCount = flow.get("LuefterON");

if (msg.payload >= myCount) {
    msg.color = "RED";
}
else {
    msg.color = "GREEN";
}

